<?phpinclude_once('Controller/C_Base.php');//// Конттроллер страницы информации о магазине.//class C_About extends C_Base{	//	// Конструктор.	//	function __construct()	{		        parent::__construct();	}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		parent::OnInput();		$this->title = 'О нас';	}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array();			$this->content = $this->Template('View/V_About.php', $vars);		parent::OnOutput();	}	}