<?phpinclude_once('C_Base.php');//// Конттроллер страницы списка новых товаров.//class C_NewPro extends C_Base{	private $newPro = array();	// массив новых товаров  		//	// Конструктор.	//	function __construct()	{    	parent::__construct();	}		//	// Виртуальный обработчик запроса.	//	protected function OnInput()	{		parent::OnInput();		$this->title = 'Новые товары';		if (isset ($_GET['cat']))		{			$this->newPro = $this->product->GetByCat($_GET['cat']);			$cat = M_Category::Instance()->Get($_GET['cat']);					$this->title = $cat['cat_name'];		}		else 			$this->newPro = $this->product->All();			}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$vars = array('admin' => $_SESSION['admin'], 'title' => $this->title, 'newPro' => $this->newPro);			$this->content = $this->Template('View/V_NewPro.php', $vars);		parent::OnOutput();	}	}