<?php/** * Шаблон страницы отображения корзины * =================================== * $sum - полная стоимость заказа * $masPro - массив товаров */?><div class="bag">	<form method="post">		<p class="cent"><input type="submit" name="delbag" value="очистить корзину" class="button"/></p><br/>	</form>	<p class="cent">Всего к оплате: <?=$sum?> руб.</p>		<?foreach ($masPro as $val):?>		<table border =1>				<tr>					<td rowspan=2><a href="index.php?str=Bag&del=1&id=<?=$val['id_pro']?>"class="fdown">Х</a>&nbsp;&nbsp;&nbsp;&nbsp;<?=$val['name']?></td>					<td> &nbsp;&nbsp;Количество</td>					<td> &nbsp;&nbsp;Цена</td>				</tr>				<tr>					<td> 						<form method="post">							<a>&nbsp;&nbsp;</a>							<input type="hidden" name="idcolpro" value="<?=$val['id_pro']?>">							<input name="col" value="<?=$val['col']?>" size="2"/>							<input type="submit" name="bcolpro" value="ok"/>						</form>					</td>					<td> &nbsp;&nbsp;						<?=$val['price']?>					</td>				</tr>		</table>	<?endforeach;?>	</br>	<form method="post">		<p class="cent"><input type="submit" name="delbag" value="Оформить заказ" class="button"/></p><br/>	</form></div>