<?php/** * Шаблон страницы списка товаров * ======================= * $newPro - массив товаров * $admin - проверка является ли пользователь админом */?><?if($admin == true):?>    <p class="cent"><a href="index.php?str=Pro&new=1" class="button">Добавить товар</a></p><?endif?><?foreach ($newPro as $val):?>	<div class="prod">    	<h3><a href="index.php?str=Pro&id=<?=$val['id_pro']?>"><?=$val['pro_name']?></a></h3>		<img src="<?=$val['img']?>"/>		<p class="cent">Цена:</p><br/>		<h4 class="cent"><?=$val['price']?> руб.</h4><br/>	</div><?endforeach;?>