<?php/** * Шаблон главной страницы * ======================= * $cat - выбранная категория * $all_cat - Массив категорий * $pro - выбранный товар */?><div class="about_prod">	<form method="post">		<p class="cent"><input type="submit" name="prodel" value="Удалить товар" class="button"/></p>	</form>	<form method="post" enctype='multipart/form-data'>		<p class="cent">			Выбрать категорию			<select name="id_cat">				<option selected value='<?=$cat['id_cat']?>'><?=$cat['cat_name']?></option>				<?foreach ($all_cat as $val):?>					<option value='<?=$val['id_cat']?>'><?=$val['cat_name']?></option>				<?endforeach;?>			</select>			<input type="submit" name="bcat" value="ОК"/><br/>		</p>		<? if ($cat['id_cat'] != null): ?>			<img src="<?=$pro['img']?>"/>			<p>Изобhажение</p>			<input type="file" name="somename" />			<br/>			<p>Имя продукта</p>			<input value="<?=$pro['pro_name']?>" name="pro_name"/><br/>			<p>Цена</p>			<input value="<?=$pro['price']?>" name="price"><br/>			<div>			<h4>Обзор и Характеристики</h4>			<table>				<tr><td><?=$cat['har1_n']?>:</td><td> &nbsp;&nbsp;<input value="<?=$pro['har1']?>" name="har1"></td></tr>				<tr><td><?=$cat['har2_n']?>:</td><td> &nbsp;&nbsp;<input value="<?=$pro['har2']?>" name="har2"></td></tr>				<tr><td><?=$cat['har3_n']?>:</td><td> &nbsp;&nbsp;<input value="<?=$pro['har3']?>" name="har3"></td></tr>				<tr><td><?=$cat['har4_n']?>:</td><td> &nbsp;&nbsp;<input value="<?=$pro['har4']?>" name="har4"></td></tr>				<tr><td><?=$cat['har5_n']?>:</td><td> &nbsp;&nbsp;<input value="<?=$pro['har5']?>" name="har5"></td></tr>			</table>						<p>Описание товара</p> 			<textarea rows="7" size="30" name="text">				<?=$pro['text']?>			</textarea>			<br/>			<p class="cent"><input type="submit" name="setpro" value="Обработать" class="button"/></p>		<? endif ?>	</form></div>