<?php/** * Шаблон страницы регистрации * ================ * $text - текст статьи * $error - текст ошибки (null, если нет ошибки) */?><div class="reg">	<form method="post">		<? if ($error != null): ?>			Ошибка: <b><?=$error?></b>		<? endif ?>		<br/>		<p class="reg">*Имя</p>		<p class="in_reg"><input type="text" name="name" value="<?=$name?>"/></br></p>		<p class="reg"> *Фамилия</p>		<p class="in_reg"><input type="text" name="fam" value="<?=$fam?>"/></br></p>		<p class="reg">*Логин</p>		<p class="in_reg"><input type="text" name="login" value="<?=$login?>"/></br></p>		<p class="reg">*Логин повторно</p>		<p class="in_reg"><input type="text" name="relogin" value=""/></br></p>		<p class="reg">*Пароль</p>		<p class="in_reg"><input type="text" name="pass" value="<?=$pass?>"/></br></p>		<p class="reg">*Пароль повторно</p>		<p class="in_reg"><input type="text" name="repass" value=""/></br></p>			<p class="reg">Телефон</p>		<p class="in_reg"><input type="text" name="phone" value="<?=$phone?>"/></br></p>			<p class="cent"><input type="submit" name="reg" value="Готово" class="button"/></p>	</form></div>